INSERT INTO `wp_comments` VALUES(230, 202, 'Manoj', 'csa.manoj@gmail.com', '', '112.79.37.171', '2015-05-30 23:25:13', '2015-05-30 17:55:13', 'So many times it has happened that I apologize to set rest an escalation without minding that the mistake was from other side but the effect was opposite,  I was further put down by the person and supported by mob mentality because there is a general perception that the person apologizing is the person who erred... Next time I ve to think twice...', 0, '1', 'Mozilla/5.0 (Linux; Android 5.0.2; A0001 Build/LRX22G) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/43.0.2357.78 Mobile Safari/537.36', '', 0, 0);
INSERT INTO `wp_comments` VALUES(231, 202, 'Stuti Khosla', 'khoslastuti16@gmail.com', '', '117.197.73.62', '2015-05-31 00:43:05', '2015-05-30 19:13:05', 'I completely agree with you, Lata di. And the solution to this will come only when we start right from scratch.', 0, '1', 'Mozilla/5.0 (Linux; Android 5.0.2; XT1033 Build/LXB22.46-28) AppleWebKit/537.36 (KHTML, like Gecko) Chrome/42.0.2311.111 Mobile Safari/537.36', '', 229, 2);
